-- map variables
Levels.map_w = #Levels.map[1]
Levels.map_h = #Levels.map
Levels.map_x = 4
Levels.map_y = 4
Levels.map_offset_x = -40
Levels.map_offset_y = -40
Levels.map_display_w = 21
Levels.map_display_h = 16
Levels.tile_w = 40
Levels.tile_h = 40

function Levels:draw_map()
   for y=1, Levels.map_display_h do
      for x=1, Levels.map_display_w do                                                         
         love.graphics.draw( 
            tile[Levels.map[y+Levels.map_y][x+Levels.map_x]], 
            (x*Levels.tile_w)+Levels.map_offset_x, 
            (y*Levels.tile_h)+Levels.map_offset_y )
      end
   end
end